mapper.update:
UPDATE FREE_BOARD SET 
CATE_NO = #{cateNo}
, TITLE = #{title}
, CONTENT = #{content}
, MODIFY_DATE = SYSDATE 
WHERE NO = #{no}

mapper.select:
SELECT FB.*, FLH.*, (COALESCE(FLH.LIKES, 0) - COALESCE(FLH.HATES, 0)) AS LH_COUNT, M.*, FBC.*, CMT.count AS CMT_COUNT FROM FREE_BOARD FB LEFT JOIN ( SELECT BOARD_NO, SUM(CASE WHEN STATUS = 'L' THEN 1 ELSE 0 END) AS LIKES, SUM(CASE WHEN STATUS = 'H' THEN 1 ELSE 0 END) AS HATES FROM FREE_BOARD_LIKE_HATE GROUP BY BOARD_NO) FLH ON FB.NO = FLH.BOARD_NO JOIN (SELECT NO, ID, NICK WRITER_NICK, IMG_PATH WRITER_IMG FROM MEMBER) M ON FB.WRITER = M.NO JOIN (SELECT NO, NAME CATE FROM FREE_BOARD_CATEGORY) FBC ON FBC.NO = FB.CATE_NO LEFT JOIN (SELECT BOARD_NO, COUNT(*) as COUNT FROM FREE_BOARD_CMT GROUP BY BOARD_NO) CMT ON FB.NO = CMT.BOARD_NO
WHERE FB.CATE_NO = #{cate}
ORDER BY FB.NO DESC

mapper.update:
UPDATE FREE_BOARD
SET HIT = HIT + 1
WHERE NO = #{bno}

mapper.insert:
INSERT INTO FREE_BOARD_CMT VALUES ( SEQ_FREEBOARD_CMT_NO.NEXTVAL, #{boardNo}, #{writer}, #{content}, SYSDATE, 'N', SYSDATE, 0 1 ,
SEQ_FREEBOARD_CMT_NO.NEXTVAL
#{group}
)
