mapper.update:
UPDATE MEMBER 
SET EMAIL_AUTH='1'
WHERE ID=#{email}

UPDATE MEMBER 
SET QUIT_YN='Y'
WHERE ID=#{id}

mapper.delete:
DELETE FROM FOLLOW
WHERE FOLLOWING_NO=#{followingNo}
AND FOLLOWER_NO=#{followerNo}

mapper.select:
SELECT COUNT(*)
FROM FOLLOW
WHERE FOLLOWER_NO = #{no}

SELECT COUNT(*)
FROM FOLLOW
WHERE FOLLOWING_NO = #{no}

	 SELECT COUNT(*)
	 FROM FOLLOW
	 WHERE FOLLOWING_NO=#{followingNo}
     AND FOLLOWER_NO=#{followerNo}
