mapper.select:
SELECT * FROM (SELECT N.NO,N.CATE_NO, C.NAME as CATE_NAME,N.TITLE,N.ENROLL_DATE, N.HIT, M.NICK AS WRITER,N. CATEGORY,N.keyword FROM NOTICE N JOIN COMMON_CATEGORY C ON N.CATE_NO= C.NO JOIN MEMBER M ON M.NO = N.WRITER WHERE STATUS = 2 and CATE_NO = #{cateNo} ORDER BY N.ENROLL_DATE DESC) WHERE ROWNUM<=3
UNION SELECT N.NO,N.CATE_NO,C.NAME as CATE_NAME,N.TITLE,N.ENROLL_DATE, N.HIT, M.NICK AS WRITER ,N.CATEGORY,N.keyword FROM NOTICE N JOIN COMMON_CATEGORY C ON N.CATE_NO= C.NO JOIN MEMBER M ON M.NO = N.WRITER WHERE CATE_NO = ${cateNo}
and ${category} LIKE '%${keyword}%'

mapper.update:
UPDATE NOTICE
SET HIT = HIT + 1
WHERE NO = #{no}
