mapper.insert:
INSERT INTO TAG (NO, NAME) SELECT SEQ_TAG_NO.NEXTVAL, A FROM (
SELECT #{arr} AS A FROM DUAL WHERE NOT EXISTS (SELECT NAME FROM TAG WHERE NAME = #{arr})
)

INSERT INTO KNOWLEDGE_TAG (A_NO, TAG_NO) SELECT (SELECT NO FROM KNOWLEDGE_QUESTION ORDER BY NO DESC OFFSET 0 ROWS FETCH NEXT 1 ROWS ONLY), A FROM (
SELECT (SELECT NO FROM TAG WHERE NAME = #{arr}) AS A FROM DUAL
)

mapper.select:
SELECT T.NO, T.NAME, K.A_NO, K.TAG_NO FROM KNOWLEDGE_TAG K
JOIN TAG T ON K.TAG_NO = T.NO
WHERE K.A_NO = #{qno}

mapper.update:
UPDATE KNOWLEDGE_QUESTION
SET CATE_NO = #{cateNo}
, TITLE = #{title}
, CONTENT = #{content}
, MODIFY_DATE = SYSDATE
WHERE NO = #{no}

UPDATE KNOWLEDGE_QUESTION SET
HIT = HIT + 1
WHERE NO = #{qno}

UPDATE KNOWLEDGE_QUESTION SET
DELETE_YN = 'Y'
WHERE NO = #{qno}

mapper.select:
SELECT STATUS FROM KNOWLEDGE_LIKE_HATE
WHERE NO = #{qno}
AND M_NO = #{mno}

SELECT Q_NO, M_NO FROM KNOWLEDGE_SCRAP
WHERE Q_NO = #{qno}
AND M_NO = #{mno}
