mapper.insert:
INSERT INTO TAG (NO, NAME) SELECT SEQ_TAG_NO.NEXTVAL, A FROM (
SELECT #{arr} AS A FROM DUAL WHERE NOT EXISTS (SELECT NAME FROM TAG WHERE NAME = #{arr})
)

INSERT INTO KNOWLEDGE_TAG (A_NO, TAG_NO) SELECT (SELECT NO FROM KNOWLEDGE_QUESTION ORDER BY NO DESC OFFSET 0 ROWS FETCH NEXT 1 ROWS ONLY), A FROM (
SELECT (SELECT NO FROM TAG WHERE NAME = #{arr}) AS A FROM DUAL
)
